import React, { useState, useEffect } from 'react';

const DataManager = () => {
    const [data, setData] = useState([]);
    const [newData, setNewData] = useState('');
    const [error, setError] = useState('');

    // Fetch data from the backend
    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/curd/getdata');
            const result = await response.json();
            if (response.ok) {
                setData(result);
            } else {
                console.error('Failed to fetch data:', result.message || response.statusText);
                setError(result.message || 'Failed to fetch data');
            }
        } catch (error) {
            console.error('Error fetching data:', error);
            setError('Failed to fetch data');
        }
    };

    // Add new data
    const addData = async () => {
        if (!newData) {
            setError('Please enter some data');
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/api/curd/adddata', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ name: newData })
            });

            const result = await response.json();

            if (response.ok) {
                fetchData(); // Refresh the data list
                setNewData('');
                setError('');
            } else {
                console.error('Failed to add data:', result.message || response.statusText);
                setError(result.message || 'Failed to add data');
            }
        } catch (error) {
            console.error('Error adding data:', error);
            setError('Failed to add data');
        }
    };

    // Delete data by ID
    const deleteData = async (id) => {
        try {
            const response = await fetch('http://localhost:5000/api/curd/deletedata', {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                    'id': id // Send ID as header
                }
            });

            if (response.ok) {
                fetchData(); // Refresh the data list
                setError('');
            } else {
                const result = await response.json();
                console.error('Failed to delete data:', result.message || response.statusText);
                setError(result.message || 'Failed to delete data');
            }
        } catch (error) {
            console.error('Error deleting data:', error);
            setError('Failed to delete data');
        }
    };

    // Use useEffect to fetch data when the component mounts
    useEffect(() => {
        fetchData();
    }, []);

    return (
        <div>
            <h1>Data Manager</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <div>
                <input
                    type="text"
                    value={newData}
                    onChange={(e) => setNewData(e.target.value)}
                    placeholder="Enter new data"
                />
                <button onClick={addData}>Add Data</button>
            </div>

            <ul>
                {data.map((item) => (
                    <li key={item.id}>
                        {item.name} (ID: {item.id})
                        <button onClick={() => deleteData(item.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DataManager;
