import React, { useState, useEffect } from 'react';

const DataManager = () => {
    const [data, setData] = useState([]);
    const [newData, setNewData] = useState('');
    const [error, setError] = useState('');

    // Fetch data from the backend on component mount
    useEffect(() => {
        fetchData();
    }, []);

    // Function to fetch data
    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/curd/getdata');
            const result = await response.json();
            setData(result);
        } catch (error) {
            console.error('Error fetching data:', error);
            setError('Failed to load data');
        }
    };

    // Function to add data
    const addData = async () => {
        if (!newData) {
            setError('Please enter some data');
            return;
        }
        
        try {
            const response = await fetch('http://localhost:5000/api/curd/getdata', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ name: newData }) // Modify the key if needed
            });

            if (response.ok) {
                fetchData(); // Refresh the data list
                setNewData('');
            } else {
                console.error('Failed to add data');
                setError('Failed to add data');
            }
        } catch (error) {
            console.error('Error adding data:', error);
            setError('Failed to add data');
        }
    };

    // Function to delete data by ID
    const deleteData = async (id) => {
        try {
            const response = await fetch('http://localhost:5000/api/curd/deletedata', {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                    'id': id // Send the id in the header
                }
            });

            if (response.ok) {
                fetchData(); // Refresh the data list
            } else {
                console.error('Failed to delete data');
                setError('Failed to delete data');
            }
        } catch (error) {
            console.error('Error deleting data:', error);
            setError('Failed to delete data');
        }
    };

    return (
        <div>
            <h1>Data Manager</h1>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            <div>
                <h2>Add New Data</h2>
                <input
                    type="text"
                    value={newData}
                    onChange={(e) => setNewData(e.target.value)}
                    placeholder="Enter new data"
                />
                <button onClick={addData}>Add Data</button>
            </div>

            <div>
                <h2>Current Data</h2>
                {data.length > 0 ? (
                    <ul>
                        {data.map((item) => (
                            <li key={item.id}>
                                {item.name} (ID: {item.id})
                                <button onClick={() => deleteData(item.id)}>Delete</button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No data available</p>
                )}
            </div>
        </div>
    );
};

export default DataManager;
